WITH REQUEST_TABLE AS (
  SELECT
    CAST('2022-04-20 10:00:00' AS TIMESTAMP) AS "POINT_IN_TIME",
    'C1' AS "CUSTOMER_ID",
    '2022-05-01' AS "FORECAST_POINT"
), _FB_AGGREGATED AS (
  SELECT
    REQ."POINT_IN_TIME",
    REQ."CUSTOMER_ID",
    REQ."FORECAST_POINT"
  FROM REQUEST_TABLE AS REQ
)
SELECT
  AGG."POINT_IN_TIME",
  AGG."CUSTOMER_ID",
  AGG."FORECAST_POINT",
  CAST(DATE_PART(day, "FORECAST_POINT") AS BIGINT) AS "forecast_point_day"
FROM _FB_AGGREGATED AS AGG
